mapper.select:
select e.picgroupreview,e.picgrouptitle,p.picorganizeid,p.picid,p.picname,h.piccontent,p.piclike,h.picphotoid,h.picphotourl,h.picuplodperson from enj_pictheme e,enj_picgroup p,enj_picphoto h where e.picgroupid=p.picorganizeid and p.piccover=h.picphotoid
and e.pictypeid=#{id}
limit #{start},#{end}
